// Root directory
define('TL_ROOT', dirname(__FILE__));

// Workaround for missing posix_getpwuid function
if (!function_exists('posix_getpwuid')) {
	function posix_getpwuid($int) {
		return array('name'=>$int);
	}
}

/**
 * Class Contao_Check
 *
 * Check a Contao installation for missing and corrupt files.
 * @copyright  Leo Feyer 2008-2011
 * @author     Leo Feyer <http://www.contao.org>
 * @package    Library
 */
class Contao_Check
{

	/**
	 * Initialize variables
	 */
	protected $version = '2.10.4';
	protected $soap = false;
	protected $safeModeHack = false;
	protected $isWindows = false;
	protected $folderPermissions = array();
	protected $filePermissions = array();

	/**
	 * Check the operating system
	 */
	public function __construct() {
		if (strtoupper(substr(PHP_OS, 0, 3)) === 'WIN') {
			$this->filePermissions = array(666);
			$this->folderPermissions = array(777);
			$this->isWindows = true;
		} else {
			$this->filePermissions = array(664, 644, 660, 640);
			$this->folderPermissions = array(775, 755, 750);
		}
	}

	/**
	 * Return the current version
	 * @return string
	 */
	public function getVersion() {
		return $this->version;
	}

	/**
	 * Return the path to the current folder
	 * @return string
	 */
	public function getPath() {
		return str_replace('contao-check.php', '', __FILE__);
	}

	/**
	 * Return true if soap is enabled
	 * @return boolean
	 */
	public function hasSoap() {
		return $this->soap;
	}

	/**
	 * Return true if there is a Contao installation
	 * @return boolean
	 */
	public function hasFiles() {
		return (file_exists(TL_ROOT . '/system/contao.css'));
	}

	/**
	 * Return true if the Safe Mode Hack is required
	 * @return boolean
	 */
	public function requiresSafeModeHack() {
		return $this->safeModeHack;
	}

	/**
	 * Check all PHP extensions and return the result as string
	 * @return string
	 */
	public function checkPhpConfiguration() {
		$return  = '<table class="extensions">';
		$return .= '<tr>';
		$return .= '<th>Parameter</th>';
		$return .= '<th class="dot" style="width:1%;">•</th>';
		$return .= '<th>Value</th>';
		$return .= '<th>Description</th>';
		$return .= '</tr>';

		// PHP version
		$ok = version_compare(phpversion(), '5.2.0', '>=');
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>PHP version</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . phpversion() . '</td>';
		$return .= '<td>Contao requires PHP 5.2 or greater.</td>';
		$return .= '</tr>';

		// Safe mode
		$safe_mode = ini_get('safe_mode');
		$ok = ($safe_mode == '' || $safe_mode == 0 || $safe_mode == 'Off');
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>Safe mode</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($safe_mode ? 'On' : 'Off') . '</td>';
		$return .= '<td>Recommended setting is Off.</td>';
		$return .= '</tr>';

		if ($safe_mode) {
			$this->safeModeHack = true;
		}

		// Maximum execution time
		$max_execution_time = ini_get('max_execution_time');
		$ok = ($max_execution_time >= 30);
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>Maximum execution time</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . $max_execution_time . '</td>';
		$return .= '<td>Recommended setting is 30 or greater.</td>';
		$return .= '</tr>';

		// Memory limit
		$memory_limit = ini_get('memory_limit');
		$ok = (intval($memory_limit) >= 16);
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>Memory limit</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . $memory_limit . '</td>';
		$return .= '<td>Recommended setting is 16M or greater.</td>';
		$return .= '</tr>';

		// Register globals
		$register_globals = ini_get('register_globals');
		$ok = ($register_globals == '' || $register_globals == 0 || $register_globals == 'Off');
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>Register globals</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($register_globals ? 'On' : 'Off') . '</td>';
		$return .= '<td>Recommended setting is Off.</td>';
		$return .= '</tr>';

		// File uploads
		$file_uploads = ini_get('file_uploads');
		$ok = ($file_uploads == 1 || $file_uploads == 'On');
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>File uploads</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($file_uploads ? 'On' : 'Off') . '</td>';
		$return .= '<td>Recommended setting is On.</td>';
		$return .= '</tr>';

		// Upload maximum filesize
		$upload_max_filesize = ini_get('upload_max_filesize');
		$ok = (intval($upload_max_filesize) >= 8);
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>Upload maximum filesize</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . $upload_max_filesize . '</td>';
		$return .= '<td>Recommended setting is 8M or greater.</td>';
		$return .= '</tr>';

		// Soap
		$soap = extension_loaded('soap');
		$ok = $soap;
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td>SOAP extension</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($soap ? 'On' : 'Off') . '</td>';
		$return .= '<td>Recommended setting is On.</td>';
		$return .= '</tr>';

		if ($soap) {
			$this->soap = true;
		}

		$return .= '</table>';
		return $return;
	}

	/**
	 * Check file permissions and return the result as string
	 * @return string
	 */
	public function checkFilePermissions() {
		$return  = '<table class="permissions">';
		$return .= '<tr>';
		$return .= '<th class="nowrap">Parameter</th>';
		$return .= '<th class="dot" style="width:1%;">•</th>';
		$return .= '<th>Value</th>';
		$return .= '<th>Description</th>';
		$return .= '</tr>';

		$return .= '<tr class="ok">';
		$return .= '<td class="nowrap">Operating system</td>';
		$return .= '<td>&nbsp;</td>';
		$return .= '<td>' . PHP_OS . '</td>';
		$return .= '<td>' . php_uname() . '</td>';
		$return .= '</tr>';

		@rmdir(TL_ROOT . '/contao-check');
		@mkdir(TL_ROOT . '/contao-check');

		// Create a directory
		$create_dir = @is_dir(TL_ROOT . '/contao-check');
		$ok = $create_dir;
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td class="nowrap">Create directory</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($create_dir ? 'OK' : 'Error') . '</td>';
		$return .= '<td>' . ($create_dir ? 'Successfully created directory "contao-check"' : 'Error creating directory "contao-check"') . '</td>';
		$return .= '</tr>';

		if (!$create_dir) {
			$this->safeModeHack = true;
		} else {
			// CHMOD
			clearstatcache();
			$fileperms = decoct(@fileperms(TL_ROOT . '/contao-check') & 511);
			$ok = in_array($fileperms, $this->folderPermissions);
			$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
			$return .= '<td class="nowrap">Directory permissions</td>';
			$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
			$return .= '<td class="value">' . $fileperms . '</td>';
			$return .= '<td>Recommended directory permission setting is ' . implode(' or ', $this->folderPermissions) . '.</td>';
			$return .= '</tr>';

			$curowner = posix_getpwuid(@fileowner(TL_ROOT . '/'));
			$fileowner = posix_getpwuid(@fileowner(TL_ROOT . '/contao-check'));

			// Directory owner
			$file_owner = ($fileowner['name'] == $curowner['name']);
			$ok = $file_owner;
			$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
			$return .= '<td class="nowrap">Directory owner</td>';
			$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
			$return .= '<td class="value">' . $fileowner['name'] . '</td>';
			$return .= '<td>' . ($file_owner ? 'The new directory is owned by you.' : 'The new directory should be owned by ' . $curowner['name'] . '.') . '</td>';
			$return .= '</tr>';

			if (!$file_owner) {
				$this->safeModeHack = true;
			}
			@rmdir(TL_ROOT . '/contao-check');
		}

		@unlink(TL_ROOT . '/contao-check.txt');
		$fh = @fopen(TL_ROOT . '/contao-check.txt', 'wb');
		@fputs($fh, 'This is a test file that can be deleted.');

		// Create a new file
		$create_file = is_resource($fh);
		$ok = $create_file;
		$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
		$return .= '<td class="nowrap">Create file</td>';
		$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
		$return .= '<td class="value">' . ($create_file ? 'OK' : 'Error') . '</td>';
		$return .= '<td>' . ($create_file ? 'Successfully created file "contao-check.txt"' : 'Error creating file "contao-check.txt"') . '</td>';
		$return .= '</tr>';

		@fclose($fh);

		if (!$create_file) {
			$this->safeModeHack = true;
		} else {
			// File permissions
			clearstatcache();
			$fileperms = decoct(@fileperms(TL_ROOT . '/contao-check.txt') & 511);
			$ok = in_array($fileperms, $this->filePermissions);
			$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
			$return .= '<td class="nowrap">File permissions</td>';
			$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
			$return .= '<td class="value">' . $fileperms . '</td>';
			$return .= '<td>Recommended file permission setting is ' . implode(' or ', $this->filePermissions) . '.</td>';
			$return .= '</tr>';

			$curowner = posix_getpwuid(@fileowner(__FILE__));
			$fileowner = posix_getpwuid(@fileowner(TL_ROOT . '/contao-check.txt'));

			// File owner
			$file_owner = ($fileowner['name'] == $curowner['name']);
			$ok = $file_owner;
			$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
			$return .= '<td class="nowrap">File owner</td>';
			$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
			$return .= '<td class="value">' . $fileowner['name'] . '</td>';
			$return .= '<td>' . ($file_owner ? 'The new file is owned by you.' : 'The new file should be owned by ' . $curowner['name'] . '.') . '</td>';
			$return .= '</tr>';

			if (!$file_owner) {
				$this->safeModeHack = true;
			}
			@unlink(TL_ROOT . '/contao-check.txt');
		}

		$return .= '</table>';
		return $return;
	}

	/**
	 * Check files and return the result as string
	 * @return string
	 */
	public function checkFiles() {
		$return  = '<table class="files">';
		$return .= '<tr>';
		$return .= '<th>File</th>';
		$return .= '<th class="dot" style="width:1%;">•</th>';
		$return .= '<th>CHMOD</th>';
		$return .= '<th class="nowrap">Recommended</th>';
		$return .= '<th class="dot" style="width:1%;">•</th>';
		$return .= '<th>Owner</th>';
		$return .= '<th>Recommended</th>';
		$return .= '<th class="dot" style="width:1%;">•</th>';
		$return .= '<th>Type</th>';
		$return .= '<th>Status</th>';
		$return .= '</tr>';

		$folderowner = posix_getpwuid(@fileowner(TL_ROOT . '/'));
		$fileowner = posix_getpwuid(@fileowner(__FILE__));
		$folderpermissions = implode(', ', $this->folderPermissions);
		$filepermissions = implode(', ', $this->filePermissions);

		foreach ($this->getFileList() as $file) {
			$status = false;
			$chmod = '-';
			$owner = array('name'=>'-');
			list($file, $md5_file, $md5_code) = $file;
			$corrupt = false;

			if (file_exists(TL_ROOT . '/' . $file)) {
				$status = true;
				$chmod = decoct(@fileperms(TL_ROOT . '/' . $file) & 511);
				$owner = posix_getpwuid(@fileowner(TL_ROOT . '/' . $file));
			}

			if (is_file(TL_ROOT . '/' . $file)) {
				$corrupt = false;
				$buffer  = str_replace("\r", '', file_get_contents(TL_ROOT . '/' . $file));
				// Check the content
				if (md5($buffer) != $md5_file) {
					// Check the content without comments
					if (md5(preg_replace('@/\*.*\*/@Us', '', $buffer)) != $md5_code) {
						$corrupt = true;
					}
				}
				unset($buffer);
			}
			
			// Directory
			if (!preg_match('/\.[A-Za-z]+5?$/', $file) && !preg_match('/(README|VERSION)$/', $file)) {
				// Add 777 for temporary directories
				if (!$this->isWindows && ($file == 'system/html' || $file == 'system/tmp')) {
					$this->folderPermissions[] = 777;
					$folderpermissions .= ',777';
				}

				$ok = ($status && in_array($chmod, $this->folderPermissions) && $owner['name'] == $folderowner['name']);
				$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
				$return .= '<td>' . $file . '</td>';
				$ok = in_array($chmod, $this->folderPermissions);
				$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
				$return .= '<td' . ($ok ? '' : ' class="value"') . '>' . $chmod . '</td>';
				$return .= '<td class="nowrap">' . $folderpermissions . '</td>';
				$ok = ($owner['name'] == $folderowner['name']);
				$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
				$return .= '<td' . ($ok ? '' : ' class="value"') . '>' . $owner['name'] . '</td>';
				$return .= '<td>' . $folderowner['name'] . '</td>';
				$return .= '<td class="dot">' . ($status ? '&nbsp;' : '•') . '</td>';
				$return .= '<td>Folder</td>';
				$return .= '<td' . ($status ? '' : ' class="value"') . '>' . ($status ? 'Validated' : 'Missing') . '</td>';
				$return .= '</tr>';

				// Remove 777
				if (!$this->isWindows && ($file == 'system/html' || $file == 'system/tmp')) {
					array_pop($this->folderPermissions);
					$folderpermissions = str_replace(',777', '', $folderpermissions);
				}
			}

			// File
			else {
				$ok = ($status && !$corrupt && in_array($chmod, $this->filePermissions) && $owner['name'] == $fileowner['name']);
				$return .= '<tr class="' . ($ok ? 'ok' : 'warning') . '">';
				$return .= '<td>' . $file . '</td>';
				$ok = in_array($chmod, $this->filePermissions);
				$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
				$return .= '<td' . ($ok ? '' : ' class="value"') . '>' . $chmod . '</td>';
				$return .= '<td class="nowrap">' . $filepermissions . '</td>';
				$ok = ($owner['name'] == $fileowner['name']);
				$return .= '<td class="dot">' . ($ok ? '&nbsp;' : '•') . '</td>';
				$return .= '<td' . ($ok ? '' : ' class="value"') . '>' . $owner['name'] . '</td>';
				$return .= '<td>' . $folderowner['name'] . '</td>';
				$return .= '<td class="dot">' . (($status && !$corrupt) ? '&nbsp;' : '•') . '</td>';
				$return .= '<td>File</td>';
				$return .= '<td' . (($status && !$corrupt) ? '' : ' class="value"') . '>' . ($status ? (!$corrupt ? 'Validated' : 'Corrupt') : 'Missing') . '</td>';
				$return .= '</tr>';
			}
		}

		$return .= '</table>';
		return $return;
	}

